                </button>


</div>



 <div   >
 {this.state.arrayTarjetas.map((item) => {
  return <Tarjetas 
  key = {item.id} 
  elemento = {item} 
  /> })}
 </div>




<footer className="Footer">
 <Footer />
 </footer>


</div>

  )
}}
